package net.jeeshop.biz.cms.service;import net.jeeshop.biz.base.bean.PageBean;import net.jeeshop.biz.base.bean.PageQueryBean;import net.jeeshop.biz.base.client.BaseMapper;import net.jeeshop.biz.base.service.BaseService;import net.jeeshop.biz.cms.client.NotifyTemplateMapper;import net.jeeshop.biz.cms.model.NotifyTemplate;import net.jeeshop.biz.cms.model.NotifyTemplateExample;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * 通知模板服务类 * @author yue * 2016年1月11日 下午11:03:16 */@Servicepublic class NotifyTemplateService extends BaseService<NotifyTemplate, NotifyTemplateExample> {	 @Autowired	 private NotifyTemplateMapper notifyTemplateMapper;	/* 	 * @return 	 */	@Override	protected BaseMapper<NotifyTemplate, NotifyTemplateExample> getMapper() {		return notifyTemplateMapper;	}	/**	 * @param queryParams	 * @param pageQueryBean	 * @return	 */	public PageBean<NotifyTemplate> selectPageBean(final NotifyTemplateExample example,			PageQueryBean pageQueryBean) {		return executePageQuery(new PageQueryExecutor<NotifyTemplate>(){			@Override			public List<NotifyTemplate> executeQuery() {				return notifyTemplateMapper.selectByExample(example);			}		},pageQueryBean);	}}